import React, { useState, useEffect } from 'react';
import { getTasks, createTask, updateTask, deleteTask, toggleTaskCompletion } from '../services/api';
import TaskItem from './TaskItem';
import TaskForm from './TaskForm';
import LoadingSpinner from './LoadingSpinner';

const TaskList = () => {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editingTask, setEditingTask] = useState(null);

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        try {
            const data = await getTasks();
            setTasks(data);
            setLoading(false);
        } catch (err) {
            setError('Failed to fetch tasks');
            setLoading(false);
        }
    };

    const handleCreateTask = async (taskData) => {
        try {
            const newTask = await createTask(taskData);
            setTasks([...tasks, newTask]);
        } catch (err) {
            setError('Failed to create task');
        }
    };

    const handleUpdateTask = async (taskData) => {
        try {
            const updatedTask = await updateTask(editingTask.id, taskData);
            setTasks(tasks.map(task => task.id === editingTask.id ? updatedTask : task));
            setEditingTask(null);
        } catch (err) {
            setError('Failed to update task');
        }
    };

    const handleDeleteTask = async (id) => {
        try {
            await deleteTask(id);
            setTasks(tasks.filter(task => task.id !== id));
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    const handleToggleComplete = async (id) => {
        try {
            const updatedTask = await toggleTaskCompletion(id);
            // If the API returns the updated task, use it. Otherwise, manually toggle locally if needed,
            // but relying on API response is safer.
            // Assuming the API returns the updated task object:
            setTasks(tasks.map(task => task.id === id ? updatedTask : task));
        } catch (err) {
            setError('Failed to toggle task completion');
        }
    };

    if (loading) return <LoadingSpinner />;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="task-list-container">
            <TaskForm
                onSubmit={editingTask ? handleUpdateTask : handleCreateTask}
                initialData={editingTask}
                onCancel={() => setEditingTask(null)}
            />
            <div className="task-list">
                {tasks.map(task => (
                    <TaskItem
                        key={task.id}
                        task={task}
                        onEdit={setEditingTask}
                        onDelete={handleDeleteTask}
                        onToggleComplete={handleToggleComplete}
                    />
                ))}
            </div>
        </div>
    );
};

export default TaskList;
